mapper.select:
SELECT COUNT(*) cnt
FROM user_info
WHERE u_nick = #{nick}

mapper.update:
UPDATE user_info
SET u_nick = #{nick}
WHERE u_id = #{uId}

mapper.select:
SELECT COUNT(*) cnt
FROM user_info
WHERE u_id = #{uId} AND u_pw = #{passwd}

mapper.update:
UPDATE user_info
SET u_pw = #{passwd}
WHERE u_id = #{uId}

mapper.select:
SELECT u_id
FROM user_info
WHERE u_name = #{name} 
AND u_pnum = #{pNum}

mapper.update:
UPDATE user_info
SET u_pw = #{passwd}
WHERE u_id = #{uId}
AND u_name = #{name}
AND u_pnum = #{pNum}

mapper.select:
SELECT COUNT(*) cnt
FROM user_info
WHERE u_name = #{name}

SELECT COUNT(*) cnt
FROM user_info
WHERE u_pnum = #{pNum}

SELECT COUNT(*) cnt
FROM user_info
WHERE u_id = #{uId}
AND u_pw = #{passwd}

SELECT COUNT(*) cnt
FROM user_info
WHERE u_id = #{uId}
